## Pandas Basics ##import pandas as pd## 1 dimensional column of informationseries = pd.Series(['Toyota', 'Bugatti', 'Tesla', 'Lambhorgini', 'BMW', 'Honda'])print('\nSeries:\n', series)colors = pd.Series(['Red', 'Blue', 'Black', 'Purple', 'Green', 'White'])print('\nColors:\n', colors)## 2 dimensional column of informationcars = pd.DataFrame({'Car': series, 'Color': colors})print('\nCar DataFrame:\n', cars)## Import datacar_sales = pd.read_csv('../data/car-sales.csv')print('\nCar Sales:\n', car_sales)## Export a dataframecars.to_csv('cars.csv')cars.to_excel('cars.xlsx')## Describe data# Attributesprint('\nCar Sales Attribute Types:\n', car_sales.dtypes)# Columnsprint('\nCar Sales Columns:\n', car_sales.columns)# Indexprint('\nCar Sales Index:\n', car_sales.index)# General informationprint('\nCar Sales Description:\n', car_sales.describe())print('\nCar Sales Info:\n', car_sales.info)# Detailed informationprint('\nSum:\n', car_sales.sum())print('\nMean:\n', car_sales.mean(numeric_only=True))print('\nCar Doors Sum:\n', car_sales.Doors.sum())